package newbegining;

public class bbb {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=7;
		
		System.out.println((a<b)? true:false);// literal form
		
		System.out.println((a>b)? "true":"false");
		
		System.out.println((a>b)? "shubham":"shivanjali");
		System.out.println((a==b)? "shubham":"shivanjali");
		
		
		//System.out.println((a<b)? jay:veeru);// jay and veeru are not variable
		System.out.println((a<b)? 1:0);
		System.out.println((a<b)? 111.5:50.9);
		//System.out.println((var1 logical operator var2)? any var:any var2);
	}

}
