package newbegining;

public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		 int a=10;
	// static int b=10; in method only local are allowed
		
	   // a++;
	    System.out.println(a);	//11
	    //--a;
	    System.out.println(--a);	//10.... print
	    System.out.println(a);	//10	.. use
	
System.out.println(a++);	//10  use.... 
System.out.println(a);	//11 		print..
		
		
	}

}
